settings.css:
.chat-line { background: #1A2233; border-radius: .25rem; padding: 0.2rem .8rem; }

.chat-line .username { max-width: 15ch;
display: inline-block;
overflow: hidden;
white-space: nowrap;
text-overflow: ellipsis;
vertical-align: text-bottom; }

.chat-line:nth-child(4n + 1) .username,

.chat-line:nth-child(4n + 1) .colon { color: #3cff64 !important; }

.chat-line:nth-child(4n + 2) .username,

.chat-line:nth-child(4n + 2) .colon { color: #7026F4 !important; }

.chat-line:nth-child(4n + 3) .username,

.chat-line:nth-child(4n + 3) .colon { color: #FEF878 !important; }

.chat-line:nth-child(4n + 4) .username,

.chat-line:nth-child(4n + 4) .colon { color: #D7A7BE !important; }

body { background-color: rgba(0, 0, 0, 0); margin: 0px auto; overflow: hidden; }

footer { display: none !important; }
.sc-itybZL { display: none !important; }
* { background: transparent !important; }
li { font-size: 1.5rem; line-height: initial !important; margin-bottom: 10px !important; }
li > div { background: rgba(255, 255,255, .7) !important; }
li > span { display: none !important; }
li > div > div > div > div { font-size: 1rem !important; }
svg, img { display: none !important; }
